import { createSlice } from "redux-toolkit";

const notif = createSlice({
  name: "notif",
  initialState: null,
  reducers: {
    setNotif(state, action) {
      console.log(action);
      return action.payload;
    },
  },
});

export const { setNotif } = notif.actions;

export default notif.reducers;
