import { useState } from "react";

const Blog = ({ blog }) => {
  const [view, setView] = useState(false);
  const addInfo = () => {
    return (
      <>
      {blog.author}<br/>
      {blog.url}<br/>
      {blog.likes} <button>like</button><br/>
      {blog.user.username}<br/> 
      </>
    )
  }
  return (
    <div>
      Title : {blog.title} <>-------</> author :  <button type="submit" onClick={() => setView(true)}>view</button>
      {view ? addInfo() : <></>}
    </div>
  );
};

export default Blog;
