import { createSlice } from "@reduxjs/toolkit";
import blogServices from "../services/blogs";


const blog = createSlice({
  name : "blog",
  initialState: [],
  reducers : {
    setBlogList(state, actions){

       const resp = blogServices.getAll().then((response) => response);
      return resp.then((values) => values);
    }
  }
  })
export const fetchBlogList = () => {
  return async (dispatch) => {
    dispatch(fetchBlogListStart());
    try {
      const response = await blogServices.getAll();
      dispatch(fetchBlogListSuccess(response));
    } catch (error) {
      dispatch(fetchBlogListFailure(error.message));
    }
  };
};

export const {setBlogList} = blog.actions

export default blog.reducer;
