import { useState, useEffect } from "react";
import Blog from "./components/Blog";
import blogService from "./services/blogs";
import login from "./services/login";
import AddedBlog from "./components/AddedBlog";
import LoginForm from "./components/LoginForm";
const App = () => {
  const [blogs, setBlogs] = useState([]);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [user, setUser] = useState(null);
  const [title, setTitle] = useState("");
  const [author, setAuthor] = useState("");
  const [url, setUrl] = useState("");
  const [message, setMessage] = useState(null);
  const [styleMsg, setStyleMsg] = useState("");
  const [loginVisible, setLoginVisible] = useState(false);
  const [newBlogForm, setNewBlogForm] = useState(false);
  useEffect(() => {
    blogService.getAll().then((blogs) => setBlogs(blogs));
    const storageLocale = window.localStorage.getItem("connectedUser");
    if (storageLocale) {
      const user = JSON.parse(storageLocale);
      setUser(user);
      blogService.setToken(user.token);
    }
  }, []);
  const handleLogin = async (event) => {
    event.preventDefault();
    console.log(username, password);
    login({ username, password })
      .then((response) => {
        window.localStorage.setItem("connectedUser", JSON.stringify(response));
        setUser(response);
        setUsername("");
        setPassword("");
        setMessage("connected");
        setStyleMsg("validationMessage");
        setLoginVisible(false);
      })
      .catch(() => {
        setMessage("wrong username or password");
        setStyleMsg("errorMessage");
      })
      .finally(() => {
        setTimeout(() => {
          setMessage(null);
        }, 1500);
      });
  };
  const handleLogOut = () => {
    window.localStorage.clear();
    setUser(null);
  };
  const handleNewBlog = async (event) => {
    event.preventDefault();
    blogService
      .create({ title, author, url })
      .then((savedBlog) => {
        setBlogs(blogs.map((blog) => blog).concat(savedBlog));
        setTitle("");
        setAuthor("");
        setUrl("");
        setMessage(`a new blog ${savedBlog.title} by ${savedBlog.author}`);
        setStyleMsg("validationMessage");
        setNewBlogForm(false);
      })
      .catch((error) => {
        setMessage(`Failed To Add Blog`);
        setStyleMsg("errorMessage");
      })
      .finally(() => {
        setTimeout(() => {
          setMessage(null);
        }, 1500);
      });
  };
  const logOutButton = () => {
    return (
      <>
        <span> {user.name} Logged In</span>{" "}
        <button type="submit" onClick={handleLogOut}>
          logout
        </button>
      </>
    );
  };
  const logInForm = () => {
    const hideWhenVisible = { display: loginVisible ? "none" : "" };
    const showWhenVisible = { display: loginVisible ? "" : "none" };
    const hideNewBlog = { display: newBlogForm ? "none" : ""}
    const showNewBlog = { display: newBlogForm ? "" : "none"}

    return (
      <div>
        <div style={showWhenVisible}>
          <LoginForm
            handleLogin={handleLogin}
            username={username}
            handleUsernameChange={({ target }) => setUsername(target.value)}
            password={password}
            handlePassChange={({ target }) => setPassword(target.value)}
            message={message}
            styleMsg={styleMsg}
            cancel={() => setLoginVisible(false)}
          />
        </div>

        <div style={hideWhenVisible}>
          <h1>Blogs</h1>
          <AddedBlog message={message} style={styleMsg} />
          {user !== null ? logOutButton() : <></>}
          <button type="submit" onClick={() => setLoginVisible(true)}>
            log-IN
          </button>
          <div style={hideNewBlog}>
          <button type="submit" onClick={() => setNewBlogForm(true)}>
            new blog
          </button>
          </div>
          <div style={showNewBlog}>
          <h1> Create New :</h1>
          <form onSubmit={handleNewBlog}>
            title :{" "}
            <input
              type="text"
              value={title}
              onChange={({ target }) => setTitle(target.value)}
            />
            <br />
            author :{" "}
            <input
              type="text"
              value={author}
              onChange={({ target }) => setAuthor(target.value)}
            />
            <br />
            url :{" "}
            <input
              type="text"
              value={url}
              onChange={({ target }) => setUrl(target.value)}
            />
            <br />
            <button type="sumbit">create</button>
          </form>
          <button type="submit" onClick={() => setNewBlogForm(false)}>
            cancel
          </button>
          {blogs.map((blog) => (
            <Blog key={blog.id} blog={blog} username={username} />
          ))}
          </div>

        </div>
      </div>
    );
  };
  return logInForm();
};

export default App;
