import "../blogcss.css";
const Blog = ({ blog }) => {
  const [view, setView] = useState(false);
  console.log(blog);
  const addInfo = () => {
    return (
      <div className="blog-info">
        {blog.author}
        <br />
        {blog.url}
        <br />
        {blog.likes} <button>like</button>
        <br />
        {blog.user.username}
        <br />
        <button type="sumbit" onClick={() => setView(false)}>
          close
        </button>
      </div>
    );
  };
  return (
    <div>
      <div className="blog-name">
        {" "}
        Title : {blog.title} <>-------</> author :{" "}
        <button type="submit" onClick={() => setView(true)}>
          view
        </button>
      </div>
      {view ? addInfo() : <></>}
    </div>
  );
};

export default Blog;
