import { useState, useEffect } from "react";
import Blog from "./components/Blog";
import blogService from "./services/blogs";
import login from "./services/login";

const App = () => {
  const [blogs, setBlogs] = useState([]);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [user, setUser] = useState(null);
  const [title, setTitle] = useState("");
  const [author, setAuthor] = useState("");
  const [url, setUrl] = useState("");
  useEffect(() => {
    blogService.getAll().then((blogs) => setBlogs(blogs));
    const storageLocale = window.localStorage.getItem("connectedUser");
    if(storageLocale){
      const user = JSON.parse(storageLocale)
      setUser(user)
      blogService.setToken(user.token)
    }
  }, []);
  const handleLogin = async (event) => {
    event.preventDefault();
    console.log(username, password)
    const connectedUser = await login({username, password})
    window.localStorage.setItem("connectedUser", JSON.stringify(connectedUser))
    setUser(connectedUser);
    setUsername("")
    setPassword("")
  };
  const handleLogOut = () => {
    window.localStorage.clear();
    setUser(null);
  }
  const handleNewBlog = async (event) => {
    event.preventDefault();
    try {
      const savedBlog = await blogService.create({title, author, url})
      console.log(savedBlog)
    } catch (error) {
      console.error(error)
    }
  }
  const logInForm = () => {
    return (
      <>
        <h1>Log-In </h1>
        <form onSubmit={handleLogin}>
          username:
          <input
            type="text"
            name="username"
            placeholder="username"
            value={username}
            onChange={({target})=> setUsername(target.value)}
          /><br/>
          password :
          <input
            type="password"
            name="password"
            placeholder="password"
            value={password}
            onChange={({target})=> setPassword(target.value)}
          /><br/>
          <button type="submit">logIn</button>
        </form>
      </>
    );
  };
  const blogView = () => {
    return (
      <div>
        <h1>Blogs</h1>
        <span> {user.name} Logged In</span> <button type="submit" onClick={handleLogOut}>logout</button>
        {blogs.map(blog => <Blog key={blog.id} blog={blog} username={username}/>)}
        <h1> Create New :</h1>
        <form onSubmit={handleNewBlog}>
         title : <input type="text" value={title} onChange={({target} )=> setTitle(target.value)}/><br />
         author : <input type="text" value={author} onChange={({target} )=> setAuthor(target.value)}/><br />
         url : <input type="text" value={url} onChange={({target} )=> setUrl(target.value)}/><br />
         <button type="sumbit">create</button>
        </form>
      </div>
    )
  }

  return (
    <div>
      {user === null ? logInForm():blogView()}
    </div>
  );
};

export default App;
