import { useState } from "react";
import "../blogcss.css";
const Blog = ({ blog, handleUpdate }) => {
  const [view, setView] = useState(false);
  const updateBlog = () => {
    const newBlog = {
      title : blog.title,
      author : blog.author,
      likes : Number(blog.likes) +1,
      user : blog.user.id
    }
    handleUpdate(blog.id,newBlog);
  }

  const addInfo = () => {
    return (
      <div className="blog-info">
        {blog.author}
        <br />
        {blog.url}
        <br />
        likes {blog.likes} <button onClick={updateBlog}>like</button>
        <br />
        {blog.user.username}
        <br />
      </div>
    );
  };
  return (
    <div>
      <div className="blog-name">
        {" "}
        Title : {blog.title}{" "}
        <button type="submit" onClick={() => setView(!view)}>
          view
        </button>
      </div>
      {view ? addInfo() : <></>}
    </div>
  );
};

export default Blog;
